def get_macd(price, slow, fast, smooth):
    exp1 = price.ewm(span = fast, adjust = False).mean()
    exp2 = price.ewm(span = slow, adjust = False).mean()
    macd = pd.DataFrame(exp1 - exp2).rename(columns = {'Close':'macd'})
    signal = pd.DataFrame(macd.ewm(span = smooth, adjust = False).mean()).rename(columns = {'macd':'signal'})
    hist = pd.DataFrame(macd["macd"] - signal["signal"]).rename(columns = {0:'hist'})
    frames =  [macd, signal, hist]
    df = pd.concat(frames, join = 'inner', axis = 1)
    return df

def plot_macd(prices, macd, signal, hist):
    ax1 = plt.subplot2grid((8,1), (0,0), rowspan = 5, colspan = 1)
    ax2 = plt.subplot2grid((8,1), (5,0), rowspan = 3, colspan = 1)

    ax1.plot(prices)
    ax2.plot(macd, color = 'grey', linewidth = 1.5, label = 'MACD')
    ax2.plot(signal, color = 'skyblue', linewidth = 1.5, label = 'SIGNAL')

    for i in range(len(prices)):
        if str(hist[i])[0] == '-':
            ax2.bar(prices.index[i], hist[i], color = '#ef5350')
        else:
            ax2.bar(prices.index[i], hist[i], color = '#26a69a')

    plt.legend(loc = 'lower right')
    plt.show()

def get_rsi(close, lookback = 14):
    ret = close.diff()
    up = []
    down = []
    for i in range(len(ret)):
        if ret[i] < 0:
            up.append(0)
            down.append(ret[i])
        else:
            up.append(ret[i])
            down.append(0)
    up_series = pd.Series(up)
    down_series = pd.Series(down).abs()
    up_ewm = up_series.ewm(com = lookback - 1, adjust = False).mean()
    down_ewm = down_series.ewm(com = lookback - 1, adjust = False).mean()
    rs = up_ewm/down_ewm
    rsi = 100 - (100 / (1 + rs))
    rsi_df = pd.DataFrame(rsi).rename(columns = {0:'rsi'}).set_index(close.index)
    rsi_df = rsi_df.dropna()
    return rsi_df[3:]

def rsi_graphmaker(tic_rsi):
    ax2 = plt.subplot2grid((10,1), (5,0), rowspan = 4, colspan = 1)
    ax2.plot(tic_rsi['rsi'], color = 'orange', linewidth = 2.5)
    ax2.axhline(30, linestyle = '--', linewidth = 1.5, color = 'grey')
    ax2.axhline(70, linestyle = '--', linewidth = 1.5, color = 'grey')
    ax2.set_title('RELATIVE STRENGTH INDEX')
    plt.show()

def graph_maker(todays , time):
    start = datetime.today() - timedelta(days = time)
    end = datetime.today()
    ticker_1 = yf.download(todays[0],start,end)
    ticker_2 = yf.download(todays[1],start,end)
    ticker_3 = yf.download(todays[2],start,end)
    #Getting Ticker MACD ( Moving average convergence divergence) Data
    ticker_1_macd = get_macd(ticker_1['Close'], 26, 12, 9)
    ticker_2_macd = get_macd(ticker_2['Close'], 26, 12, 9)
    ticker_3_macd = get_macd(ticker_3['Close'], 26, 12, 9)

    #Getting RSI(Relative Strength Index) data
    ticker_1_rsi = get_rsi(ticker_1['Close'])
    ticker_2_rsi = get_rsi(ticker_2['Close'])
    ticker_3_rsi = get_rsi(ticker_3['Close'])

    #
    print(todays[0])
    plot_macd(ticker_1['Close'], ticker_1_macd['macd'], ticker_1_macd['signal'], ticker_1_macd['hist'])
    rsi_graphmaker(ticker_1_rsi)
    print(todays[1])
    plot_macd(ticker_2['Close'], ticker_2_macd['macd'], ticker_2_macd['signal'], ticker_2_macd['hist'])
    rsi_graphmaker(ticker_2_rsi)
    print(todays[2])
    plot_macd(ticker_3['Close'], ticker_3_macd['macd'], ticker_3_macd['signal'], ticker_3_macd['hist'])
    rsi_graphmaker(ticker_3_rsi)

graph_maker(todays, 365)
